
CREATE TABLE IF NOT EXISTS `zupal_place_countries` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `name` varchar(255) NOT NULL,
  `code` varchar(3) NOT NULL,
  `has_states` tinyint(4) NOT NULL default '1',
  `lat` float NOT NULL,
  `long` float NOT NULL,
  PRIMARY KEY  (`id`),
  KEY `code` (`code`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=247 ;

